section "game/modules/tome/data/talents/chronomancy/spacetime-weaving.lua"

t("Dimensional Step", "空间跳跃")
t("You do not have line of sight.", "你没有视线")
t("#Source# folds space with with #target#!", "#Source# 折叠了与 #target# 之间的空间!")
