section "game/modules/tome/data/talents/psionic/psionic.lua"

t("Absorb damage and gain energy.", " 吸收伤害并获得能量。")
t("absorption", "吸收")
t("Project energy to damage foes.", " 使用能量摧毁目标。")
t("projection", "投射")
